import React, {useState} from 'react';
import { data } from '../../../data';

const UseStateArray = () => {

  const [peoples, setPeoples] = useState(data);

  return <>
    {
      peoples.map((people) => {
        const {id, name} = people;
        return (<div key={id} className="item">
          <h4>{name}</h4>
        </div>
        );
      })
    }
  </>;
};

export default UseStateArray;
