import React, { useState, List } from 'react';
import { data } from '../../../data';
// more components
// fix - context api, redux (for more complex cases)

const PropDrilling = () => {
  const [people, setPeople] = useState(data);
  return <section>
    <h3>Prop Drilling</h3>
    <List people={people} />
  </section>
  return <h2>prop drilling</h2>;
};

export default PropDrilling;
