import React, { useState } from 'react';

const UseStateObject = () => {
  const [person, setPerson] = useState({name: "Ahsan", age: 24, message: 'random message'});


  return <>
    <h3>{person.name}</h3>
    <h3>{person.message}</h3>
    <h3>{person.age}</h3>
  </>;
};

export default UseStateObject;
